<?php/** * Photon CMS * * @copyright  Copyright (c) 2009 Martin Bazik * @package    Receptar *//** * Homepage presenter. * * @author     Martin Bazik * @package    Receptar */class Front_HomePagePresenter extends Front_BasePresenter{	public function actionHomepage()	{		$homepage = $this->model('pages')->getHomepage();		if($homepage === false) $this->view = 'blank';		if($homepage->has_widgets == true) 		{			$tpl = new StringTemplate($this);			$homepage->content = $tpl->setTemplate($homepage->content)->__toString();		}		$this->template->data = $homepage;		$homepage = null;		}}