<?php/** * Photon CMS * * @copyright  Copyright (c) 2009 Martin Bazik * @package    Receptar *//** * Homepage presenter. * * @author     Martin Bazik * @package    Receptar */class Front_HomePagePresenter extends Front_BasePresenter{    public function actionDefault()    {        $this->actionHomepage();    }        public function actionHomepage()    {        $homepage = $this->model('pages')->getHomepage();        if($homepage === false) $this->view = 'blank';        else        {                        $this->data = $homepage;            $this->template->data = $homepage;            $homepage = null;        }    }}